import './App.css';
import axios from 'axios';
import Top from './Components/Top';
import Middle from './Components/Middle';
import Bottom from './Components/Bottom';
import { useState } from 'react';

function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=857736f25b8c1622a8f6997beb913e45`;
  const searchLocation = (e)=>{

    if(e.key === 'Enter')
    {
      console.log(location);
      axios.get(url).then((response)=>{
        setData(response.data);
      })
    }
  }
  return (
    <div className="App">
      <Top setLocation = {setLocation} searchLocation = {searchLocation}/>
      {data.main?<Middle temperature = {data.main.temp} />:<Middle />}
      {data.main&&data.wind?<Bottom windSpeed={data.wind.speed} humidity = {data.main.humidity} pressure = {data.main.pressure}/>: <Bottom />}
    </div>
  );
}

export default App;
